"use strict";

var curry = require("../Strict/curry.js");

var go = require("../Strict/go.js");

var mapLazy = require("../Lazy/mapLazy.js");

var takeUntilLazy = require("../Lazy/takeUntilLazy.js");

var reduceC = require("./reduceC.js");

var not = require("../Strict/not.js");

module.exports = curry(function everyC(f, iter) {
  return go(mapLazy(f, iter), takeUntilLazy(not), reduceC((a, b) => a && b), (a = false) => a, Boolean);
});