"use strict";

var curry = require("../Strict/curry.js");

var emptyLazy = require("./emptyLazy.js");

var mapLazy = require("./mapLazy.js");

var rangeLazy = require("./rangeLazy.js");

module.exports = curry(function splitEveryLazy(n, str) {
  if (!str) return emptyLazy();
  return mapLazy(i => str.substr(i * n, n), rangeLazy(Math.ceil(str.length / n)));
});