"use strict";

var dropWhileLazy = require("../Lazy/dropWhileLazy.js");

var curry = require("./curry.js");

var takeAll = require("./takeAll.js");

var go1 = require("./go1.js");

module.exports = curry(function dropWhile(f, iter) {
  return go1(iter, _iter => takeAll(dropWhileLazy(f, _iter)));
});