"use strict";

var curry = require("./curry.js");

var go = require("./go.js");

var reduce = require("./reduce.js");

var not = require("./not.js");

var mapLazy = require("../Lazy/mapLazy.js");

var takeUntilLazy = require("../Lazy/takeUntilLazy.js");

module.exports = curry(function every(f, iter) {
  return go(mapLazy(f, iter), takeUntilLazy(not), reduce((a, b) => a && b), (a = false) => a, Boolean);
});