"use strict";

var curry = require("./curry.js");

var go = require("./go.js");

var identity = require("./identity.js");

var reduce = require("./reduce.js");

var mapLazy = require("../Lazy/mapLazy.js");

var takeUntilLazy = require("../Lazy/takeUntilLazy.js");

module.exports = curry(function some(f, iter) {
  return go(mapLazy(f, iter), takeUntilLazy(identity), reduce((a, b) => a || b), (a = false) => a, Boolean);
});