"use strict";

var curry = require('./curry.js');

var zipLazy = require('../Lazy/zipLazy.js');

var takeAll = require("./takeAll.js");

var go = require("./go.js");

var apply = require('./apply.js');

module.exports = curry(function zip(...iters) {
  return go(iters, takeAll, apply(zipLazy), takeAll);
});